mapper.select:
select count(*)
from  post
where userid=#{userid}

		select *
		from  ( 
			SELECT rownum AS rnum, temp.*
    		FROM (
        	SELECT title, postid, cdate, views
        	FROM post
       		WHERE userid = 50
        	ORDER BY postid DESC
    	) temp
			)
		WHERE rnum >= #{startNum}
		  AND rnum <= #{endNum}

		select *
		from  ( 
			SELECT rownum AS rnum,
       			  p.title, p.postid, p.cdate, m.nickname
   	 		FROM likes l
			LEFT OUTER JOIN post p ON(l.postid = p.postid) 
			LEFT OUTER JOIN member m ON(m.userid = p.userid) 
			where userid=#{userid}
        	ORDER BY l.cdate DESC
        	)
		WHERE rnum >= #{startNum}
		  AND rnum <= #{endNum}

select count(*)
from  likes
where userid=#{userid}

	select *
		from  ( 
			select rownum AS rnum, temp.*
            from
            (SELECT 
       			  p.title, p.postid, p.cdate, m.nickname, p.views, p.mnp
   	 		FROM post p
			LEFT OUTER JOIN member m ON(m.userid = p.userid) 
        	ORDER BY p.views DESC
        	) temp)
		WHERE rnum >= #{startNum}
		  AND rnum <= #{endNum}
