mapper.select:
select *
from(select rownum AS rnum, mnp, title, postid
from post
where info like '%' || #{info} || '%'
 ORDER BY postid 
)
WHERE rnum >=#{startNum}
AND rnum <=#{endNum}

select *
from(select rownum AS rnum,temp.*
	 from (select mnp, title, postid
	       from post
	       where info like '%' || #{info} || '%'
		   ORDER BY postid DESC
		   ) temp
          )
WHERE rnum >=#{startNum}
AND rnum <=#{endNum}
