mapper.select:
select count(*)
from member
where id=#{id}

select count(*)
from member
where nickname=#{nickname}

select count(*)
from member
where id=#{id} and pw=#{pw}

select id
from member
where email=#{email} and nickname=#{nickname}

select count(*)
from member
where id= #{id} and email=#{email} and nickname=#{nickname}

select pw
from member
where id= #{id} and email=#{email} and nickname=#{nickname}

select pw
from member
where userid = #{userid}

mapper.update:
UPDATE member
SET pw = #{pw}
WHERE userid = #{userid}
