mapper.select:
		select *
		from  ( 
			SELECT rownum AS rnum,
       			  title, postid, cdate, views
   	 		FROM
        		   post 
			where userid=#{userid} 
        	ORDER BY postid DESC
        	)
		WHERE rnum >= #{startNum}
		  AND rnum <= #{endNum}

select count(*)
from  post
where userid=#{userid}
