mapper.select:
SELECT count(*)
FROM  post
WHERE userid=#{userid}

		SELECT *
		FROM  ( 
			SELECT rownum AS rnum, temp.*
    		FROM (
        	SELECT title, postid, cdate, views
        	FROM post
       		WHERE userid = 50
        	ORDER BY postid DESC
    	) temp
			)
		WHERE rnum >= #{startNum}
		  AND rnum <= #{endNum}

		SELECT *
		FROM (
    			SELECT rownum AS rnum, temp.*
    			FROM(
   					 SELECT p.title, p.postid, p.cdate, m.nickname,p.views
    				 FROM likes l
  				     LEFT OUTER JOIN post p ON l.postid = p.postid 
    			     LEFT OUTER JOIN member m ON m.userid = p.userid 
   					 WHERE l.userid = #{userid}
   					 ORDER BY l.cdate DESC) temp
				) 
		WHERE rnum >= #{startNum}
		  AND rnum <= #{endNum}

SELECT count(*)
FROM  likes
WHERE userid=#{userid}
